import React, { useState, useEffect } from "react";
import { jwtDecode } from "jwt-decode";

const PomodoroTimer = () => {
  const [username, setUsername] = useState(null);
  const [timeLeft, setTimeLeft] = useState(25 * 60);
  const [isRunning, setIsRunning] = useState(false);
  const [cycle, setCycle] = useState("work");

  // üîπ –î–µ–∫–æ–¥–∏—Ä–∞—ö–µ –Ω–∞ JWT —Ç–æ–∫–µ–Ω–æ—Ç
  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setUsername(decoded.sub || decoded.username || decoded.user || "guest");
      } catch (err) {
        console.error("Invalid token:", err);
      }
    }
  }, []);

  // üîπ –°–∫–ª–∞–¥–∏—Ä–∞—ö–µ –Ω–∞ —Ç–∞—ò–º–µ—Ä —Å–æ—Å—Ç–æ—ò–±–∞ –ø–æ –∫–æ—Ä–∏—Å–Ω–∏–∫
  const storageKey = `pomodoro_${username}`;

  // üîπ –í—á–∏—Ç—É–≤–∞—ö–µ —Å–æ—Å—Ç–æ—ò–±–∞ –ø—Ä–∏ refresh
  useEffect(() => {
    if (!username) return;
    const saved = localStorage.getItem(storageKey);
    if (saved) {
      const { timeLeft, isRunning, cycle, lastUpdate } = JSON.parse(saved);
      const now = Date.now();
      const elapsed = Math.floor((now - lastUpdate) / 1000);
      const newTime = isRunning ? Math.max(timeLeft - elapsed, 0) : timeLeft;
      setTimeLeft(newTime);
      setIsRunning(isRunning);
      setCycle(cycle);
    }
  }, [username]);

  // üîπ –ê–≤—Ç–æ–º–∞—Ç—Å–∫–æ –∑–∞—á—É–≤—É–≤–∞—ö–µ —Å–µ–∫–æ—ò–∞ –ø—Ä–æ–º–µ–Ω–∞
  useEffect(() => {
    if (!username) return;
    localStorage.setItem(
      storageKey,
      JSON.stringify({
        timeLeft,
        isRunning,
        cycle,
        lastUpdate: Date.now(),
      })
    );
  }, [timeLeft, isRunning, cycle, username]);

  // üîπ –ü–æ–º–æ–¥–æ—Ä–æ –ª–æ–≥–∏–∫–∞
  useEffect(() => {
    let timer;
    if (isRunning && timeLeft > 0) {
      timer = setInterval(() => setTimeLeft((prev) => prev - 1), 1000);
    } else if (timeLeft === 0) {
      if (cycle === "work") {
        alert("–í—Ä–µ–º–µ –µ –∑–∞ –ø–∞—É–∑–∞ ‚òï");
        setCycle("break");
        setTimeLeft(5 * 60);
      } else {
        alert("–í—Ä–µ–º–µ –µ –∑–∞ —Ä–∞–±–æ—Ç–∞ üí™");
        setCycle("work");
        setTimeLeft(25 * 60);
      }
    }
    return () => clearInterval(timer);
  }, [isRunning, timeLeft, cycle]);

  const formatTime = (seconds) => {
    const m = Math.floor(seconds / 60);
    const s = seconds % 60;
    return `${m.toString().padStart(2, "0")}:${s.toString().padStart(2, "0")}`;
  };

  if (!username) return <p>–°–µ –≤—á–∏—Ç—É–≤–∞...</p>;

  return (
    <div className="flex flex-col items-center bg-white p-6 rounded-2xl shadow-md w-80">
      <h2 className="text-xl font-semibold mb-2">
        {cycle === "work" ? "–§–æ–∫—É—Å –≤—Ä–µ–º–µ" : "–ü–∞—É–∑–∞"}
      </h2>
      <p className="text-4xl font-mono mb-4">{formatTime(timeLeft)}</p>
      <div className="flex space-x-2">
        <button
          onClick={() => setIsRunning(!isRunning)}
          className="bg-blue-500 text-white px-4 py-2 rounded-xl"
        >
          {isRunning ? "–ü–∞—É–∑–∞" : "–°—Ç–∞—Ä—Ç"}
        </button>
        <button
          onClick={() => {
            setIsRunning(false);
            setTimeLeft(25 * 60);
            setCycle("work");
            localStorage.removeItem(storageKey);
          }}
          className="bg-gray-300 px-4 py-2 rounded-xl"
        >
          –†–µ—Å–µ—Ç
        </button>
      </div>
    </div>
  );
};

export default PomodoroTimer;
