import React, { useState, useEffect } from "react";

const PomodoroTimer = ({ username }) => {
  const [timeLeft, setTimeLeft] = useState(25 * 60);
  const [isRunning, setIsRunning] = useState(false);
  const [cycle, setCycle] = useState("work");

  // –ö–ª—É—á –∑–∞ —Å–µ–∫–æ—ò –∫–æ—Ä–∏—Å–Ω–∏–∫ –ø–æ—Å–µ–±–Ω–æ
  const storageKey = `pomodoro_${username}`;

  // üü¢ –í—á–∏—Ç–∞—ò –ø–æ–¥–∞—Ç–æ—Ü–∏ –æ–¥ localStorage –∫–æ–≥–∞ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞—Ç–∞ —Å–µ –≤—á–∏—Ç–∞
  useEffect(() => {
    if (!username) return; // –∞–∫–æ –Ω–µ –µ –ª–æ–≥–∏—Ä–∞–Ω–æ
    const savedData = localStorage.getItem(storageKey);
    if (savedData) {
      const { timeLeft, isRunning, cycle, lastUpdate } = JSON.parse(savedData);

      const now = Date.now();
      const elapsed = Math.floor((now - lastUpdate) / 1000);

      if (isRunning && timeLeft - elapsed > 0) {
        setTimeLeft(timeLeft - elapsed);
      } else if (isRunning && timeLeft - elapsed <= 0) {
        setTimeLeft(0);
      } else {
        setTimeLeft(timeLeft);
      }
      setIsRunning(isRunning);
      setCycle(cycle);
    }
  }, [storageKey, username]);

  // üü° –°–µ–∫–æ—ò–ø–∞—Ç –∫–æ–≥–∞ —Å–µ –º–µ–Ω—É–≤–∞ —Å–æ—Å—Ç–æ—ò–±–∞—Ç–∞ ‚Äî –∑–∞—á—É–≤–∞—ò —ò–∞
  useEffect(() => {
    if (!username) return;
    localStorage.setItem(
      storageKey,
      JSON.stringify({
        timeLeft,
        isRunning,
        cycle,
        lastUpdate: Date.now(),
      })
    );
  }, [timeLeft, isRunning, cycle, storageKey, username]);

  // ‚è∞ –¢–∞—ò–º–µ—Ä –ª–æ–≥–∏–∫–∞
  useEffect(() => {
    let timer;
    if (isRunning && timeLeft > 0) {
      timer = setInterval(() => setTimeLeft((prev) => prev - 1), 1000);
    } else if (timeLeft === 0) {
      if (cycle === "work") {
        alert("–í—Ä–µ–º–µ –µ –∑–∞ –ø–∞—É–∑–∞! ‚òï");
        setCycle("break");
        setTimeLeft(5 * 60);
      } else {
        alert("–í—Ä–µ–º–µ –µ –∑–∞ —Ä–∞–±–æ—Ç–∞! üí™");
        setCycle("work");
        setTimeLeft(25 * 60);
      }
    }
    return () => clearInterval(timer);
  }, [isRunning, timeLeft, cycle]);

  const formatTime = (seconds) => {
    const m = Math.floor(seconds / 60);
    const s = seconds % 60;
    return `${m.toString().padStart(2, "0")}:${s.toString().padStart(2, "0")}`;
  };

  return (
    <div className="flex flex-col items-center bg-white p-6 rounded-2xl shadow-md w-80">
      <h2 className="text-xl font-semibold mb-2">
        {cycle === "work" ? "–§–æ–∫—É—Å –≤—Ä–µ–º–µ" : "–ü–∞—É–∑–∞"}
      </h2>
      <p className="text-4xl font-mono mb-4">{formatTime(timeLeft)}</p>
      <div className="flex space-x-2">
        <button
          onClick={() => setIsRunning(!isRunning)}
          className="bg-blue-500 text-white px-4 py-2 rounded-xl"
        >
          {isRunning ? "–ü–∞—É–∑–∞" : "–°—Ç–∞—Ä—Ç"}
        </button>
        <button
          onClick={() => {
            setIsRunning(false);
            setTimeLeft(25 * 60);
            setCycle("work");
            localStorage.removeItem(storageKey);
          }}
          className="bg-gray-300 px-4 py-2 rounded-xl"
        >
          –†–µ—Å–µ—Ç
        </button>
      </div>
    </div>
  );
};

export default PomodoroTimer;
