import React, { useEffect, useState } from "react";
import axios from "axios";
import dayjs from "dayjs";

export default function ToDoList({ username }) {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({
    title: "",
    description: "",
    dueDate: "",
    plannedStart: ""
  });

  useEffect(() => {
    axios.get(`http://localhost:9091/api/tasks/${username}`)
      .then(res => setTasks(res.data))
      .catch(console.error);
  }, [username]);

  const addTask = () => {
    axios.post("http://localhost:9091/api/tasks", { ...newTask, user: { username } })
      .then(res => setTasks([...tasks, res.data]));
  };

  const toggleComplete = (id) => {
    const updated = tasks.find(t => t.id === id);
    axios.put(`http://localhost:9091/api/tasks/${id}`, { ...updated, completed: !updated.completed })
      .then(res => setTasks(tasks.map(t => t.id === id ? res.data : t)));
  };

  const deleteTask = (id) => {
    axios.delete(`http://localhost:8080/api/tasks/${id}`)
      .then(() => setTasks(tasks.filter(t => t.id !== id)));
  };

  return (
    <div className="p-6 bg-white rounded-2xl shadow-lg">
      <h2 className="text-xl font-bold mb-4">To-Do List</h2>

      <div className="mb-4">
        <input
          type="text"
          placeholder="Task title"
          className="border p-2 mr-2 rounded"
          value={newTask.title}
          onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
        />
        <input
          type="datetime-local"
          className="border p-2 mr-2 rounded"
          onChange={(e) => setNewTask({ ...newTask, plannedStart: e.target.value })}
        />
        <button onClick={addTask} className="bg-blue-600 text-white px-4 py-2 rounded">
          Add
        </button>
      </div>

      <ul>
        {tasks.map(t => (
          <li key={t.id} className="flex justify-between items-center border-b py-2">
            <div>
              <input type="checkbox" checked={t.completed} onChange={() => toggleComplete(t.id)} />
              <span className={t.completed ? "line-through ml-2" : "ml-2"}>
                {t.title} ({dayjs(t.plannedStart).format("DD/MM HH:mm")})
              </span>
            </div>
            <button onClick={() => deleteTask(t.id)} className="text-red-500">Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
