import React, { useState, useEffect } from "react";

export default function PomodoroTimer() {
  const [timeLeft, setTimeLeft] = useState(25 * 60); // default 25 мин
  const [isRunning, setIsRunning] = useState(false);
  const [isBreak, setIsBreak] = useState(false);

  useEffect(() => {
    let timer;
    if (isRunning && timeLeft > 0) {
      timer = setInterval(() => setTimeLeft(timeLeft - 1), 1000);
    } else if (timeLeft === 0) {
      handleSessionEnd();
    }
    return () => clearInterval(timer);
  }, [isRunning, timeLeft]);

  const handleSessionEnd = () => {
    alert(isBreak ? "Време е за работа!" : "Време е за пауза!");
    setIsBreak(!isBreak);
    setTimeLeft(isBreak ? 25 * 60 : 5 * 60); // фокус 25мин, пауза 5мин
  };

  const toggleTimer = () => setIsRunning(!isRunning);
  const resetTimer = () => {
    setIsRunning(false);
    setIsBreak(false);
    setTimeLeft(25 * 60);
  };

  const minutes = Math.floor(timeLeft / 60);
  const seconds = timeLeft % 60;

  return (
    <div className="flex flex-col items-center gap-4 p-6 bg-white rounded-2xl shadow-md w-80">
      <h2 className="text-xl font-semibold">{isBreak ? "Пауза" : "Фокус"}</h2>
      <div className="text-4xl font-bold">
        {minutes}:{seconds.toString().padStart(2, "0")}
      </div>
      <div className="flex gap-3">
        <button
          onClick={toggleTimer}
          className="bg-blue-500 text-white px-4 py-2 rounded-xl"
        >
          {isRunning ? "Пауза" : "Старт"}
        </button>
        <button
          onClick={resetTimer}
          className="bg-gray-300 text-black px-4 py-2 rounded-xl"
        >
          Ресет
        </button>
      </div>
    </div>
  );
}
