import React, { useEffect, useState } from "react";
import instance from "../../custom-axios/axios";
import dayjs from "dayjs";

export default function ToDoList({ userId }) {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({
    title: "",
    description: "",
    dueDate: "",
    plannedStart: ""
  });

  // Fetch tasks for user
  const fetchTasks = () => {
    if (!userId) return;
    instance.get(`/tasks/${userId}`)
      .then(res => setTasks(res.data))
      .catch(err => console.error("Error loading tasks:", err));
  };

  useEffect(() => {
    fetchTasks();
  }, [userId]);

  // Add new task
  // const addTask = () => {
  //   if (!newTask.title) return; // simple validation
  //   instance.post(`/tasks/${userId}`, newTask)
  //     .then(res => {
  //       setTasks([...tasks, res.data]);
  //       setNewTask({ title: "", description: "", dueDate: "", plannedStart: "" });
  //     })
  //     .catch(err => console.error("Error adding task:", err));
  // };

  const addTask = async () => {
  const userId = localStorage.getItem("userId"); // make sure this is stored at login
  if (!userId) {
    console.error("User ID not found in localStorage");
    return;
  }

  try {
    await instance.post(`/tasks/${userId}`, newTasktask, {
      headers: {
        Authorization: `Bearer ${localStorage.getItem("token")}`
      }
    });
    console.log("Task added successfully");
  } catch (error) {
    console.error("Error adding task:", error);
  }
};


  // Mark task as complete
  const toggleComplete = (id) => {
    instance.put(`/tasks/${id}/complete`)
      .then(res => setTasks(tasks.map(t => t.id === id ? res.data : t)))
      .catch(err => console.error("Error completing task:", err));
  };

  // Delete task
  const deleteTask = (id) => {
    instance.delete(`/tasks/${userId}/${id}`)
      .then(() => setTasks(tasks.filter(t => t.id !== id)))
      .catch(err => console.error("Error deleting task:", err));
  };

  return (
    <div className="p-6 bg-white rounded-2xl shadow-lg">
      <h2 className="text-xl font-bold mb-4">To-Do List</h2>

      <div className="mb-4 flex gap-2">
        <input
          type="text"
          placeholder="Task title"
          className="border p-2 rounded flex-1"
          value={newTask.title}
          onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
        />
        <input
          type="datetime-local"
          className="border p-2 rounded"
          value={newTask.plannedStart}
          onChange={(e) => setNewTask({ ...newTask, plannedStart: e.target.value })}
        />
        <button onClick={addTask} className="bg-blue-600 text-white px-4 py-2 rounded">
          Add
        </button>
      </div>

      <ul>
        {tasks.map(t => (
          <li key={t.id} className="flex justify-between items-center border-b py-2">
            <div>
              <input
                type="checkbox"
                checked={t.completed}
                onChange={() => toggleComplete(t.id)}
              />
              <span className={t.completed ? "line-through ml-2" : "ml-2"}>
                {t.title} {t.plannedStart && `(${dayjs(t.plannedStart).format("DD/MM HH:mm")})`}
              </span>
            </div>
            <button onClick={() => deleteTask(t.id)} className="text-red-500">
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
